import { Component } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { User } from 'src/app/models/User';
import { GetUserInfoService } from 'src/app/services/get-user-info.service';
@Component({
  selector: 'app-add-user',
  templateUrl: './add-user.component.html',
  styleUrls: ['./add-user.component.scss'],
})
export class AddUserComponent {
  newUser: User = {
    id: 0,
    name: "",
    lastName: "",
    userImage: '',
    cardNumber: '',
    cardPin: '',
    cardCvv: '',
    cardExpirationDate: new Date(),
  };
  constructor(
    private formBuilder: FormBuilder,
    private router: Router,
    private userService: GetUserInfoService
  ) {

  }

  onSubmit() {
    if (this.addUserForm.invalid) {
      return;
    }

    const newUser: User = {
      name: this.addUserForm.controls['name'].value,
      lastName: this.addUserForm.controls['lastName'].value,
      cardNumber: this.addUserForm.controls['cardNumber'].value,
      cardPin: this.addUserForm.controls['cardPin'].value,
      cardCvv: this.addUserForm.controls['cardCvv'].value,
      cardExpirationDate: this.addUserForm.controls['cardExpirationDate'].value,
      userImage: this.addUserForm.controls['userImage'].value,
      id: 0,
    };

    this.userService.addUser(newUser).subscribe(() => {
      this.router.navigate(['/home']);
    });
  }
}
